--
-- Estructura de tabla para la tabla vinos
--

DROP TABLE vinos;

CREATE TABLE vinos (
  idVino varchar(10) NOT NULL,
  vino varchar(100) DEFAULT NULL,
  cosechaVino NUMBER DEFAULT NULL,
  precioVino float DEFAULT NULL,
  tipoVino varchar(50) DEFAULT NULL,
  DOVino varchar(50) DEFAULT NULL,
  paisVino varchar(50) DEFAULT NULL,
  productorVino varchar(50) DEFAULT NULL,
  uvasVino varchar(50) DEFAULT NULL,
  PRIMARY KEY (idVino)
);

--
-- Volcado de datos para la tabla vinos
--



DROP SEQUENCE idComentarioSeq;
create sequence idComentarioSeq increment by 1 minvalue 1 nomaxvalue;

--
-- Estructura de tabla para la tabla comentarios
--

DROP TABLE comentarios;
CREATE TABLE comentarios (
  id NUMBER NOT NULL,
  idVino varchar(10) DEFAULT NULL,
  valoracion NUMBER DEFAULT NULL,
  comentario varchar(255) DEFAULT NULL,
  PRIMARY KEY(id),
  FOREIGN KEY (idVino) REFERENCES vinos(idVino)
);


create or replace trigger insertarComentario
BEFORE INSERT on comentarios
for each row
begin 
select idComentarioSeq.nextval into :new.id
from dual;
end;